const Hero = (props) => {
    return (
      <header className="bg-dark text-white p-5 hero-container">
        <h1 className="hero-text">{props.text}</h1>
        <div className="hero-backdrop" style={{backgroundImage: `url(${props.backdropUrl})`}}>
            
        </div>
        <p style={{color:"white"}}>{props.backdropUrl}</p>
      </header>

    )
  }

  export default Hero;
